sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    //     alert("Thank you for loading the NeedIt form.   When you close this alert you will be able to interact with the form.");   
    var leftBucket = g_form.getElement('sb_left');
    var rightBucket = g_form.getElement('sb_right');

    while (rightBucket.firstChild) {
        rightBucket.removeChild(rightBucket.firstChild);
    }
    var values = g_form.getValue('preference_selection');
    var selectedIDs = values.split(',');
    for (var j = 0; j < selectedIDs.length; j++) {
        var selectedOptions = leftBucket.options;
        for (var i = 0; i < selectedOptions.length; i++) {
            if (selectedOptions[i].value == selectedIDs[j]) {
                rightBucket.appendChild(leftBucket[i]);
                break;
            }
        }
    }
}
